mapper.update:
UPDATE aditional_field_payment_voucher
set nombre_campo = #{nombreCampo},
    valor_campo = #{valorCampo},
    id_payment_voucher = #{idPaymentVoucher},
    type_field_id = #{typeFieldId}
WHERE id = #{id}

update.foreach:
UPDATE aditional_field_payment_voucher
SET nombre_campo = #{item.nombreCampo},
valor_campo = #{item.valorCampo},
id_payment_voucher = #{item.idPaymentVoucher},
type_field_id = #{item.typeFieldId}
WHERE id = #{item.id}
